DROP TABLE IF EXISTS `cards2`;

CREATE TABLE `cards2` (
  `set_name` enum('core','prize','tw','precon') NOT NULL DEFAULT 'core',
  `set_number` int(11) NOT NULL,
  `card_name` char(30) NOT NULL,
  `origin` enum('natural','magical','constructed') NOT NULL,
  `alignment` enum('good','evil','wild','unaligned') NOT NULL,
  `cost` enum('action','free') NOT NULL,
  `rarity` enum('common','prize','rare','ultra') NOT NULL,
  `card_type` enum('champion','object','event') NOT NULL,
  `restricted` bit(1) NOT NULL,
  `banned` bit(1) NOT NULL,
  `gift` bit(1) NOT NULL,
  `offense` int(11) DEFAULT NULL,
  `defense` int(11) DEFAULT NULL,
  `instantaneous` bit(1) NOT NULL,
  `speed` enum('fast','paced','build') NOT NULL,
  `text` text NOT NULL,
  PRIMARY KEY (`set_name`,`set_number`)
);


DROP TABLE IF EXISTS `stock2`;

CREATE TABLE `stock2` (
  `set_name` enum('core','prize','tw','precon'),
  `set_number` int(11),
  `qty` int(11) NOT NULL DEFAULT 0,
  `price` int(11) NOT NULL DEFAULT 0,
  PRIMARY KEY (`set_name`, `set_number`));


